  }),
  parameters: {
    docs: {
      description: {
        story: 'Use os controles abaixo para experimentar diferentes configurações do componente.'
      }
    }
  }
};

export const WithPagination: Story = {
  args: {
    data: sampleData,
    columns: sampleColumns,
    pageSize: 3,
  },
  render: (args) => ({
    props: args,
    template: `<bb-generic-table ${argsToTemplate(args)}></bb-generic-table>`
  }),
  parameters: {
    docs: {
      description: {
        story: `
### Tabela com Paginação

Esta story demonstra o uso da tabela com paginação habilitada, útil para grandes volumes de dados.

\`\`\`html
<bb-generic-table 
  [data]="data" 
  [columns]="columns" 
  [pageSize]="3">
</bb-generic-table>
\`\`\`
        `
      }
    }
  }
};

export const WithoutPagination: Story = {
  args: {
    data: sampleData.slice(0, 5),
    columns: sampleColumns,
    pageSize: undefined,
  },
  render: (args) => ({
    props: args,
    template: `<bb-generic-table ${argsToTemplate(args)}></bb-generic-table>`
  }),
  parameters: {
    docs: {
      description: {
        story: `
### Tabela sem Paginação

Quando \`pageSize\` é undefined, a paginação é desabilitada e todos os dados são exibidos de uma vez.

\`\`\`html
<bb-generic-table 
  [data]="data" 
  [columns]="columns">
</bb-generic-table>
\`\`\`
        `
      }
    }
  }
};

export const EmptyState: Story = {
  args: {
    data: [],
    columns: sampleColumns,
    pageSize: 10,
  },
  render: (args) => ({
    props: args,
    template: `<bb-generic-table ${argsToTemplate(args)}></bb-generic-table>`
  }),
  parameters: {
    docs: {
      description: {
        story: 'Demonstra como a tabela se comporta quando não há dados para exibir.'
      }
    }
  }
};

export const LargeDataset: Story = {
  args: {
    data: Array.from({ length: 100 }, (_, i) => ({
      id: i + 1,
      name: `User ${i + 1}`,
      email: `user${i + 1}@example.com`,
      status: i % 3 === 0 ? 'inactive' : 'active' as 'active' | 'inactive',
      amount: Math.floor(Math.random() * 5000) + 500
    })),
    columns: sampleColumns,
    pageSize: 10,
  },
  render: (args) => ({
    props: args,
    template: `<bb-generic-table ${argsToTemplate(args)}></bb-generic-table>`
  }),
  parameters: {
    docs: {
      description: {
        story: 'Demonstra o comportamento da tabela com um grande volume de dados (100 registros) e paginação.'
      }
    }
  }
};

export const ResponsiveLayout: Story = {
  args: {
    data: sampleData.slice(0, 5),
    columns: sampleColumns,
    pageSize: undefined,
  },
  render: (args) => ({
    props: args,
    template: `
      <div class="w-full">
        <bb-generic-table ${argsToTemplate(args)}></bb-generic-table>
      </div>
    `
  }),
  parameters: {
    viewport: {
      defaultViewport: 'mobile1'
    },
    docs: {
      description: {
        story: 'Visualiza como a tabela se adapta em diferentes tamanhos de tela. Use o controle de viewport para testar.'
      }
    }
  }
};

