{
  render: () => ({
    props: {
      items: [
        { id: 1, label: 'Item 1', checked: false },
        { id: 2, label: 'Item 2', checked: true },
        { id: 3, label: 'Item 3', checked: false }
      ],
      selectAll: false,
      onItemChange(index: number) {
        this['items'][index].checked = !this['items'][index].checked;
        this['updateSelectAll']();
      },
      onSelectAllChange() {
        this['selectAll'] = !this['selectAll'];
        this['items'].forEach((item: any) => item.checked = this['selectAll']);
      },
      updateSelectAll() {
        const checkedCount = this['items'].filter((item: any) => item.checked).length;
        this['selectAll'] = checkedCount === this['items'].length;
      }
    },
    template: `
      <div class="space-y-4">
        <bb-checkbox 
          label="Select All" 
          [checked]="selectAll"
          [indeterminate]="items.filter(item => item.checked).length > 0 && items.filter(item => item.checked).length < items.length"
          (click)="onSelectAllChange()"
          class="font-medium">
        </bb-checkbox>

        <div class="pl-6 space-y-2">
          @for (item of items; track item.id) {
            <bb-checkbox 
              [label]="item.label"
              [checked]="item.checked"
              (click)="onItemChange($index)">
            </bb-checkbox>
          }
        </div>

        <div class="text-sm text-gray-600 mt-4">
          Selected: {{ items.filter(item => item.checked).length }} of {{ items.length }}
        </div>
      </div>
    `
  }),
  parameters: {
    docs: {
      description: {
        story: `
### Interactive Multi-Selection

This story demonstrates a complex interaction pattern with a "Select All" checkbox that:
- Shows checked state when all items are selected
- Shows indeterminate state when some items are selected
- Shows unchecked state when no items are selected
- Updates child checkboxes when toggled

Perfect for implementing bulk actions or grouped selections.
        `
      }
    }
  }
}

