      ],
      selectAll: false,
      onItemChange(index: number) {
        this['items'][index].checked = !this['items'][index].checked;
        this['updateSelectAll']();
      },
      onSelectAllChange() {
        this['selectAll'] = !this['selectAll'];
        this['items'].forEach((item: any) => item.checked = this['selectAll']);
      },
      updateSelectAll() {
