        <bb-checkbox 
          label="Select All" 
          [checked]="selectAll"
          [indeterminate]="items.filter(item => item.checked).length > 0 && items.filter(item => item.checked).length < items.length"
          (click)="onSelectAllChange()"
          class="font-medium">
        </bb-checkbox>

        <div class="pl-6 space-y-2">
          @for (item of items; track item.id) {
            <bb-checkbox 
              [label]="item.label"
              [checked]="item.checked"
              (click)="onItemChange($index)">
            </bb-checkbox>
          }
        </div>

